import React, {Component} from 'react';
import { Link } from 'react-router-dom';

import api from '../../services/api';
import './styles.css';
import SideBar from '../../components/sidebar';
import ReactDataGrid from '@inovua/reactdatagrid-enterprise'
import '@inovua/reactdatagrid-enterprise/index.css'

import Logo from "../../images/logo.png";
import { ProSidebar, SidebarHeader, SidebarFooter, SidebarContent, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';

export default class Main extends Component {
    state = {
        usuarios: [],
        usuariosInfo: [],
        page: 1
    }
    
    componentDidMount() {
      this.loadUsuarios();  
    }

    loadUsuarios = async (page = 1) => {
      const response = await api.get('/usuario');

      const { usuarios, ...usuariosInfo } = response.data;

      this.setState({ usuarios, usuariosInfo, page });
    }
    
    nextPage = () => {
        const { page, usuariosInfo } = this.state;

        if (page === usuariosInfo.pages) return;

        const pageNumber = page + 1;

        this.loadUsuarios(pageNumber);
    }

    prevPage = () => {
        const { page, usuariosInfo } = this.state;

        if (page === 1) return;

        const pageNumber = page - 1;

        this.loadUsuarios(pageNumber);
    }

    render() {
    
      const { usuarios } = this.state;

      return (
        <div className="principal">
          <div className="lado">
            <ProSidebar>
              <SidebarHeader>
                {<div>
                   <img src={Logo} className="logo-style" alt="Logo Magna" />
                 </div>
                  }
              </SidebarHeader>
              <SidebarContent>
                {
                  <Menu iconShape="square" className="menu-style">
                    <MenuItem className="item-style">Inicio</MenuItem>
                    <SubMenu className="item-style" title="Usuários">
                      <MenuItem>Todos</MenuItem>
                      <MenuItem>Novos</MenuItem>
                    </SubMenu>
                  </Menu>
                }  
              </SidebarContent>
              <SidebarFooter>
                {/**
                 *  You can add a footer for the sidebar ex: copyright
                 */}
              </SidebarFooter>
            </ProSidebar>
          </div>

          <ReactDataGrid
            idProperty="id"
            style={{ minHeight: 550 }}
            activateRowOnFocus={true}
            columns={[
              { name: 'id', header: 'Id', defaultVisible: false, type: 'number', defaultWidth: 80 },
              { name: 'name', defaultFlex: 1, header: 'Name' },
              { name: 'country', defaultFlex: 1,
                header: 'Country',
              },
              { name: 'city', defaultFlex: 1, header: 'City' },
              { name: 'age', defaultFlex: 1, type: 'number', header: 'Age' }
            ]}
            dataSource={[
              {
                id: 0,
                firstName: 'Bill',
                name: 'Bill',
                student: true,
                age: 20,
                city: 'Manchester',
                country: 'uk',
                email: 'bill@manchester.uk',
                birthDate: 1980,
              },
              {
                id: 1,
                firstName: 'Mary',
                name: 'Mary',
                age: 22,
                student: true,
                city: 'New York',
                country: 'usa',
                email: 'mary.mary@gmail.com',
                birthDate: 1982,
              }]}
          />
          
          {usuarios.map(usuario => (
              <article key={usuario.id}>
                  <strong>{usuario.nome}</strong>
                  <p><strong>{usuario.login}</strong></p>
                  <p><strong>{usuario.telefone}</strong></p>

                  <Link to={`/usuario/${usuario.id}`}>Visualizar</Link>
              </article>
          ))}
          <div className="actions">
              <button disable={this.page === 1}onClick={this.prevPage}>Anterior</button>
              <button onClick={this.nextPage}>Próxima</button>
          </div>


        </div>
      );
    }
}
