import React, {Component} from 'react';
import { Link } from 'react-router-dom';

import api from '../../services/api';
import './styles.css';
import SideBar from '../../components/sidebar';

import Logo from "../../images/logo.png";
import { ProSidebar, SidebarHeader, SidebarFooter, SidebarContent, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';

import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from '@material-ui/core';
import Paper from '@material-ui/core/Paper';
import { DataGrid } from '@material-ui/data-grid';



const columns = [
  { field: 'id', headerName: 'ID', width: 80 },
  { field: 'nome', headerName: 'Nome', width: 200 },
  { field: 'login', headerName: 'E-mail', width: 300 },
  { field: 'telefone', headerName: 'Telefone', type: 'number', width: 200}
];

export default class Main extends Component {
  constructor(props) {
    super(props);
    
    this.state = {
      usuarios: []
    };
  }

    componentDidMount() {
      this.loadUsuarios();  
    }

    loadUsuarios = async () => {
      const response = await api.get('/usuario');

      this.setState({ usuarios:  response.data.usuarios});
    }

    render() {
      return (
        <div className="principal">
          <div className="lado">
            <ProSidebar>
              <SidebarHeader>
                {<div>
                   <img src={Logo} className="logo-style" alt="Logo Magna" />
                 </div>
                  }
              </SidebarHeader>
              <SidebarContent>
                {
                  <Menu iconShape="square" className="menu-style">
                    <MenuItem className="item-style">Inicio</MenuItem>
                    <SubMenu className="item-style" title="Usuários">
                      <MenuItem>Todos</MenuItem>
                      <MenuItem>Novos</MenuItem>
                    </SubMenu>
                    <MenuItem className="item-style">Propriedades</MenuItem>
                    <MenuItem className="item-style">Talhões</MenuItem>
                  </Menu>
                }  
              </SidebarContent>
              <SidebarFooter>
                {/**
                 *  You can add a footer for the sidebar ex: copyright
                 */}
              </SidebarFooter>
            </ProSidebar>
          </div>
          
          <div className="table-style" style={{ height: 400, width: 900 }}>
            <DataGrid rows={this.state.usuarios} columns={columns} pageSize={5} />
          </div>


        </div>
      );
    }
}
