import React, { Component } from "react";
import UploadFilesService from "../../services/upload-file";

export default class UploadFile extends Component {
    state = {
        selectedFiles: [
            {
                lastModified: 1601755522000,
                lastModifiedDate: 'Sat Oct 03 2020 17:05:22 GMT-0300 (Horário Padrão de Brasília)',
                name: "print01.png",
                size: 37474,
                type: "image/png",
                webkitRelativePath: "",
            }
        ],
        currentFile: [],
        progress: 0,
        message: "",
        fileInfos: [],
    }

    selectFile(event) {
        console.log(event.target.files);
        this.setState({
            selectedFiles: event.target.files,
        });
    }

    upload() {
        let currentFile = this.state.selectedFiles[0];
    
        this.setState({
          progress: 0,
          currentFile: currentFile,
        });
    
        UploadFilesService.upload(currentFile, (event) => {
          this.setState({
            progress: Math.round((100 * event.loaded) / event.total),
          });
        }).then((response) => {
            this.setState({
              message: response.data.message,
            });
            return UploadFilesService.getFiles();
        }).then((files) => {
            this.setState({
              fileInfos: files.data,
            });
        }).catch(() => {
            this.setState({
              progress: 0,
              message: "Could not upload the file!",
              currentFile: [],
            });
        });
    
        this.setState({
          selectedFiles: [],
        });
    }

    componentDidMount() {
        UploadFilesService.getFiles().then((response) => {
          this.setState({
            fileInfos: response.data,
          });
        });
    }

    render() {
        const {
          selectedFiles,
          currentFile,
          progress,
          message,
          fileInfos,
        } = this.state;
    
        return (
          <div>
            {currentFile && (
              <div className="progress">
                <div
                  className="progress-bar progress-bar-info progress-bar-striped"
                  role="progressbar"
                  aria-valuenow={progress}
                  aria-valuemin="0"
                  aria-valuemax="100"
                  style={{ width: progress + "%" }}
                >
                  {progress}%
                </div>
              </div>
            )}
    
            <label className="btn btn-default">
              <input type="file" onChange={this.selectFile} />
            </label>
    
            <button className="btn btn-success"
              disabled={!selectedFiles}
              onClick={this.upload}
            >
              Upload
            </button>
    
            <div className="alert alert-light" role="alert">
              {message}
            </div>
    
            <div className="card">
              <div className="card-header">List of Files</div>
              <ul className="list-group list-group-flush">
                {fileInfos &&
                  fileInfos.map((file, index) => (
                    <li className="list-group-item" key={index}>
                      <a href={file.url}>{file.name}</a>
                    </li>
                  ))}
              </ul>
            </div>
          </div>
        );
    }
}
