import React, {Component} from 'react';
import { Link } from 'react-router-dom';

import api from '../../services/api';
import './styles.css';
import SideBar from '../../components/sidebar';

import Logo from "../../images/logo.png";
import { ProSidebar, SidebarHeader, SidebarFooter, SidebarContent, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';

import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from '@material-ui/core';
import Paper from '@material-ui/core/Paper';
import { DataGrid } from '@material-ui/data-grid';



const columns = [
  { field: 'id', headerName: 'ID', width: 70 },
  { field: 'firstName', headerName: 'First name', width: 130 },
  { field: 'lastName', headerName: 'Last name', width: 130 },
  {
    field: 'age',
    headerName: 'Age',
    type: 'number',
    width: 90,
  },
  {
    field: 'fullName',
    headerName: 'Full name',
    description: 'This column has a value getter and is not sortable.',
    sortable: false,
    width: 160,
    valueGetter: (params) =>
      `${params.getValue('firstName') || ''} ${
        params.getValue('lastName') || ''
      }`,
  },
];

const rows = [
  { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
  { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
  { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
  { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
  { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: null },
  { id: 6, lastName: 'Melisandre', firstName: null, age: 150 },
  { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
  { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
  { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
];






export default class Main extends Component {
    state = {
        usuarios: [],
        usuariosInfo: [],
        page: 1
    }
    
    componentDidMount() {
      this.loadUsuarios();  
    }

    loadUsuarios = async (page = 1) => {
      const response = await api.get('/usuario');

      const { usuarios, ...usuariosInfo } = response.data;

      this.setState({ usuarios, usuariosInfo, page });
    }
    
    nextPage = () => {
        const { page, usuariosInfo } = this.state;

        if (page === usuariosInfo.pages) return;

        const pageNumber = page + 1;

        this.loadUsuarios(pageNumber);
    }

    prevPage = () => {
        const { page, usuariosInfo } = this.state;

        if (page === 1) return;

        const pageNumber = page - 1;

        this.loadUsuarios(pageNumber);
    }

    render() {
    
      const { usuarios } = this.state;

      return (
        <div className="principal">
          <div className="lado">
            <ProSidebar>
              <SidebarHeader>
                {<div>
                   <img src={Logo} className="logo-style" alt="Logo Magna" />
                 </div>
                  }
              </SidebarHeader>
              <SidebarContent>
                {
                  <Menu iconShape="square" className="menu-style">
                    <MenuItem className="item-style">Inicio</MenuItem>
                    <SubMenu className="item-style" title="Usuários">
                      <MenuItem>Todos</MenuItem>
                      <MenuItem>Novos</MenuItem>
                    </SubMenu>
                    <MenuItem className="item-style">Propriedades</MenuItem>
                    <MenuItem className="item-style">Talhões</MenuItem>
                  </Menu>
                }  
              </SidebarContent>
              <SidebarFooter>
                {/**
                 *  You can add a footer for the sidebar ex: copyright
                 */}
              </SidebarFooter>
            </ProSidebar>
          </div>
          
          <div style={{ height: 400, width: 800 }}>
            <DataGrid rows={rows} columns={columns} pageSize={5} checkboxSelection />
          </div>


        </div>
      );
    }
}
