import React, {Component} from 'react';
import api from '../../services/api';
import './styles.css';
import MenuLateral from '../../components/MenuLateral';

import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, TablePagination, Button, Modal, TextField } from '@material-ui/core';
import Paper from '@material-ui/core/Paper';
export default class Usuarios extends Component {
    state = {
        usuarios: [],
        abrirVisualizar: false,
        onlyRead: true,
        usuarioSelecionado: 0,
    }
    
    componentDidMount() {
      this.loadUsuarios();  
    }

    loadUsuarios = async (page = 1) => {
      const response = await api.get('/usuario');

      const { usuarios, ...usuariosInfo } = response.data;

      this.setState({ usuarios, usuariosInfo, page });
    }

    handleOpen = (event) => {
      console.log(event.currentTarget.id);
      this.setState({ abrirVisualizar: true });
    };

    handleOpenEditar = (event) => {
      console.log(event.currentTarget.id);
      this.setState({ abrirVisualizar: true, onlyRead: false });
    };

    handleClose = () => {
      this.setState({ abrirVisualizar: false, onlyRead: true });
    };

    render() {
    
      const { usuarios } = this.state;

      return (
        <div className="principal">
          <MenuLateral />

          <TableContainer className="table-style">
            <h1>Usuários</h1>
            <Table className={{minWidth: 650}} aria-label="simple table">
              <TableHead>
                <TableRow className="cabecalho">
                <TableCell align="left">ID</TableCell>
                  <TableCell align="center">Nome</TableCell>
                  <TableCell align="center">Email</TableCell>
                  <TableCell align="center">Telefone</TableCell>
                  <TableCell align="center">Ações</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {usuarios.map((usuario) => (
                  <TableRow key={usuario.id}>
                    <TableCell align="th">{usuario.id}</TableCell>
                    <TableCell align="center">{usuario.nome}</TableCell>
                    <TableCell align="center">{usuario.login}</TableCell>
                    <TableCell align="center">{usuario.telefone}</TableCell>
                    <TableCell align="center">
                      <Button id={usuario.id} variant="outlined" className="table-button" onClick={this.handleOpen}>Visualizar</Button>
                      <Button id={usuario.id} variant="outlined" className="table-button" onClick={this.handleOpenEditar}>Editar</Button>
                      <Button id={usuario.id} variant="outlined" className="table-button">Excluir</Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
          
          <Modal
            open={this.state.abrirVisualizar}
            onClose={this.handleClose}
            aria-labelledby="simple-modal-title"
            aria-describedby="simple-modal-description">
            {
              <div className="modalVisualizar">
                <div>
                  <h1>Visualizar Informações do Usuário</h1>
                </div>
                <form className="formulario" noValidate autoComplete="off">
                  <div>
                    <TextField 
                      className="formulario-campo"
                      label="Nome"
                      defaultValue="Emerson Hoffmann"
                      InputProps={{
                        readOnly: this.state.onlyRead,
                        className: "formulario-campo"
                      }}
                    />
                    <TextField
                      label="Senha"
                      type="password"
                      autoComplete="current-password"
                      defaultValue="testeee"
                      InputProps={{
                        readOnly: this.state.onlyRead,
                        className: "formulario-campo"
                      }}
                    />
                    <div className="separador">
                    <TextField 
                      label="E-mail"
                      type="email"
                      defaultValue="emerson.hoffmann@gmail.com"
                      InputProps={{
                        readOnly: this.state.onlyRead,
                        className: "formulario-campo-email"
                      }}
                    />
                    </div>
                  </div>
                  <div className="separador">
                    <TextField 
                      label="Telefone"
                      type="telephone"
                      defaultValue="(45) 98822-8830"
                      InputProps={{
                        readOnly: this.state.onlyRead,
                        className: "formulario-campo"
                      }}
                    />
                    <TextField 
                      label="Data/Hora Registro"
                      type="text"
                      defaultValue="11/11/2020 18:56:54"
                      InputProps={{
                        readOnly: this.state.onlyRead,
                        className: "formulario-campo"
                      }}
                    />
                    <TextField 
                      label="Administrador"
                      type="bool"
                      defaultValue="false"
                      InputProps={{
                        readOnly: this.state.onlyRead,
                        className: "formulario-campo"
                      }}
                    />
                  </div>
                </form>
              </div>
            }
          </Modal>

        </div>
      );
    }
}
