import React, {Component} from 'react';
import api from '../../services/api';
import './styles.css';
import MenuLateral from '../../components/MenuLateral';

import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, TablePagination, Button } from '@material-ui/core';
import Paper from '@material-ui/core/Paper';
export default class Usuarios extends Component {
    state = {
        usuarios: [],
        usuariosInfo: [],
        page: 1
    }
    
    componentDidMount() {
      this.loadUsuarios();  
    }

    loadUsuarios = async (page = 1) => {
      const response = await api.get('/usuario');

      const { usuarios, ...usuariosInfo } = response.data;

      this.setState({ usuarios, usuariosInfo, page });
    }
    
    nextPage = () => {
        const { page, usuariosInfo } = this.state;

        if (page === usuariosInfo.pages) return;

        const pageNumber = page + 1;

        this.loadUsuarios(pageNumber);
    }

    prevPage = () => {
        const { page, usuariosInfo } = this.state;

        if (page === 1) return;

        const pageNumber = page - 1;

        this.loadUsuarios(pageNumber);
    }

    render() {
    
      const { usuarios } = this.state;

      return (
        <div className="principal">

          <TableContainer className="table-style">
            <h1>Usuários</h1>
            <Table className={{minWidth: 650}} aria-label="simple table">
              <TableHead>
                <TableRow>
                <TableCell align="left">ID</TableCell>
                  <TableCell align="center">Nome</TableCell>
                  <TableCell align="center">Email</TableCell>
                  <TableCell align="center">Telefone</TableCell>
                  <TableCell align="center">Ações</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {usuarios.map((usuario) => (
                  <TableRow key={usuario.id}>
                    <TableCell align="th">{usuario.id}</TableCell>
                    <TableCell align="center">{usuario.nome}</TableCell>
                    <TableCell align="center">{usuario.login}</TableCell>
                    <TableCell align="center">{usuario.telefone}</TableCell>
                    <TableCell align="center">
                      <Button variant="outlined" className="table-button">Visualizar</Button>
                      <Button variant="outlined" className="table-button">Editar</Button>
                      <Button variant="outlined" className="table-button">Excluir</Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
          

        </div>
      );
    }
}
